import React,{Component} from 'react';
import md5 from 'md5-hash';
import '../css/login.css';
import axios from 'axios';

class Login extends Component
{
constructor(props)
{
    super(props)
    this.state={
        username:null,
        password:null,
		isLoggedIn:false,
		token:null,
		error:true,
    }
}

handleChange=(e)=>{
	var data=e.target.value;
	this.setState({
	  [e.target.name]:data
	})
  }


handleSubmit=(e)=>{
	e.preventDefault();
	axios.post("http://127.0.0.1:8000/owner/login_user/", {'username':this.state.username,'password':this.state.password})
    .then(response => {console.log(response)})
    .catch(error => {
        console.log(error);
	});

	// this.setState({
	//   isLoggedIn: true
	// })
	// sessionStorage.setItem('username', this.state.email);
	// sessionStorage.setItem('isLoggedIn',true)
	// this.props.history.push('/home')
  }

render()
{
    return(
        <div className="container">

  {this.state.error?(()=>{return(<div class='alert alert-danger'>
	  <strong>ERROR!</strong> This alert box could indicate a dangerous or potentially negative action.
	  </div>)}):(()=>{reuturn("")})}
	<div className="d-flex justify-content-center h-100">
		<div className="card">
			<div className="card-header">
				<h3>Sign In</h3>
			</div>
			<div className="card-body">
				<form onSubmit={this.handleSubmit} id="login_form">
					<div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fas fa-user"></i></span>
						</div>
						<input onChange={ this.handleChange } type="text" className="form-control" id="username" name="username" placeholder="username" required></input>

					</div>
					<div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fas fa-key"></i></span>
						</div>
						<input onChange={ this.handleChange } type="password" className="form-control" id="password" name="password" placeholder="password" required></input>
					</div>
					<div className="row align-items-center remember">
						<label>remember me</label><input type="checkbox"></input>
					</div>
					<div className="form-group">
						<input type="submit" value="Login" className="btn-lg float-right login_btn"/>
					</div>
				</form>
			</div>
		</div>
	</div>
</div>
    )
}
}
export default Login;
