import React, { Component } from 'react';
import { Link, NavLink, withRouter, Router } from 'react-router-dom';
import '../css/home.css';
import Menu from './menu.component'
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";
import axios from 'axios';



class Home extends Component {
    constructor(props) {
        super(props)




        if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
            this.props.history.push('/')
        }
        this.state = {
            fname: "",
            lname: "",
            mobile: "",
            payment_method: "",
            items: [
                {
                    "id": 1,
                    "item": "",
                    "brand":"",
                    "size":"",
                    "quantity": 0,
                    "selling_price": 0,
                    "deleteable": false
                }
            ],
            discount_given: 0,
            total_amount:0,
            total_discount: 0,
            amount_payable: 0,
            payment_options: [],
            categories: [],
            sizes: [],
            errors: [{status:200,error:"error",message:"error message"},{status:200,error:"error",message:"error message"}]
        }
        const headers = { 'Authorization': 'Token ' + sessionStorage.getItem('token') }

        axios.get('http://127.0.0.1:8000/payments/', { headers: headers })
            .then(response => {
                console.log(response)
                var errors=[]
                if(response.data.status === 404)
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Please add some payment options"}
                    errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }
                else if(response.data.status === 400)
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Unknown error occured while fetching payment options"}
                    errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }
                else if(response.data.state === 200)
                {
                this.setState({ payment_options: response.data.Data })
                }
                else
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Unknown error occured while fetching payment options"}
                    errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }
            })
            .catch(err=>{
                let error={"status":"","error":err,"message":"Unknown error occured while getting payment options"}
                let errors = [...this.state.errors, error];
                this.setState({errors:errors})
            }
            )

        axios.get('https://shield.pythonanywhere.com/items/itemsBrand/', { headers: headers })
            .then(response => {
                if(response.data.status === 404)
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Please add some Items and Brands options"}
                   let errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }
                else if(response.data.status === 400)
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Unknown error occured while Items and Brands options"}
                    let errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }
                else if(response.data.state === 200)
                {
                    let categories=response.data.Data
                    this.setState({ categories: [{'id':-1,'name':"none","brands":[]},...categories.slice(0)] })
                }
                else
                {
                    let error={"status":response.data.status,"error":response.data.error,"message":"Unknown error occured while fetching Items and Brands options"}
                    let errors = [...this.state.errors, error];
                    this.setState({errors:errors})
                }

            })
            .catch(err=>{
                let error={"status":"","error":err,"message":"Unknown error occured while fetching Items and Brands options"}
               let  errors = [...this.state.errors, error];
                this.setState({errors:errors})
            }
            )

        axios.get('https://shield.pythonanywhere.com/sizes/', { headers: headers })
            .then(response => {

                this.setState({ sizes: response.data.Data })
            }) 
            let money=this.find_sum()
            this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})

        const items = this.state.items.length ? (this.state.items.map(item => {
            let brands = this.state.categories.filter(category => {

                if (item.item == category.name) {
                    return category
                }
            })
           })):("")
    }

    get_pdf = (e) => {
        e.preventDefault();
        var data=this.state
        this.state.payment_options.map(option=>{
            if(option.name === data.payment_method)
            {
                data.payment_method=option.id
            }
        })
data.items.map(item=>{
    data.categories.map(category=>{
        if(item.item === category.name)
        {
            item.item=category.id
            category.brands.map(brand=>{
                    if(brand.name === item.brand)
                    {
                        item.brand=brand.id
                    }

            })
        }
    })
})
console.log(data);
        const headers = {
            'Authorization': 'Token ' + sessionStorage.getItem('token')
        }
        axios.post('https://shield.pythonanywhere.com/receipts/', data, {
            headers: headers,
        })
            .then(response => {
                console.log(response)
                window.open("https://shield.pythonanywhere.com" + response.data.Data.receipt_link);
            })
            .catch(error => {
                console.log(error)
            });
    }




    handleChange = (e) => {
        var data = e.target.value;
        var money=this.find_sum()
        this.setState({
            [e.target.name]: data
        },()=>{let money=this.find_sum();this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})})
    }


    add_item = () => {
        let item = {
            "id": this.state.items.length + 1,
            "item": "",
            "brand":"",
            "size":"",
            "quantity":0,
            "selling_price": 0,
            "deleteable": true,
        }
        let items = [...this.state.items, item];
        this.setState({
            items: items
        });
    };


    delete_item = (e) => {
        let key= e.currentTarget.parentNode.parentNode.getAttribute("data-key");
        let items = this.state.items.filter(item => {
            return parseInt(item.id) !== parseInt(key);
        })

        this.setState({items: items},()=>{ let money=this.find_sum();this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})})
    }


    update_item =   (e) =>  {
       let key  =   e.currentTarget.parentNode.getAttribute("data-key");
       let name =   e.currentTarget.getAttribute("name")
       let value    =   e.currentTarget.value
       let price=0
       let quantity=0
       if(value === "")
       {
           return;
       }
       const items =  this.state.items.length?  (this.state.items.map(item => {
           if (parseInt(item.id) === parseInt(key))
           {
               item[name]=value;
           }
           return(item)
       })):([])
       let money=this.find_sum()
       this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})

    }



    getBrands = (id) => {
        const brands = this.state.categories.filter(category => {
            return parseInt(category.id) == parseInt(id)
        })
        return (brands[0].brands)
    }


    find_sum = (quantity,value) => {
        var money=new Array(3).fill(0)
        var discount=parseInt(this.state.discount_given?(this.state.discount_given):0)
        this.state.items.forEach((item) => {
            let price = parseInt(item.selling_price)
            let quantity = parseInt(item.quantity)
            money[0] = parseInt(money[0]) + parseInt(price) * parseInt(quantity)
        });
        money[1]=Math.round((discount*money[0])/100.0)
        money[2]=money[0]-money[1]
        return money;
    }

    calculate = (amount) => {
        let discount = this.state.discount_given;
        if (discount == '') {
            discount = 0;
        }
        let reduced = (discount * amount / 100)
        let payable = amount - reduced;
        document.getElementById('amount').value = payable;
    }



render()
{
    const errors= this.state.errors.length?(this.state.errors.map(error=>{
        return(<div className="alert alert-warning alert-dismissible fade show" role="alert">
        <button type="button" className="close" data-dismiss="alert" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
        <strong>Holy guacamole!</strong> You should check in on some of those fields below.
      </div>)
    })) :("")

    const payments = this.state.payment_options.length ? (this.state.payment_options.map(option => {
        return (<option key={option.id}>{option.name}</option>)
    })) : ("");

    const categories = this.state.categories.length ? (this.state.categories.map(category => {
        return (<option key={category.id}>{category.name}</option>)
    })) : ("");

    const sizes = this.state.sizes.length ? (this.state.sizes.map(size => {
        return (<option key={size.id}>{size.name}</option>)
    })) : (" ")


    const items = this.state.items.length ? (this.state.items.map(item => {
        const temp = this.state.categories.filter(category => {

            if (item.item == category.name) {
                return category
            }


        })
        let brands=""
        try
        {
            brands = temp[0].brands.length ? (temp[0].brands.map(brand => {
                return (<option key={brand.id}>{brand.name}</option>)
            })) : (" ")
        }
        catch(e)
        {
            console.log(e)
        }

        return (<div data-key={item.id} className="input-group form-group row item-list">
            <div className="input-group-prepend">
                <span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
            </div>
            <select onChange={this.update_item} type="text" value={item.item} className="form-control item" name="item" placeholder="Item" required>{categories}</select>
            <select onChange={this.update_item} type="text" value={item.brand} className="form-control item" name="brand" placeholder="Brand" required><option>None</option>{brands}</select>
            <input onChange={this.update_item}  type="text" value={item.size} className="form-control item" name="size" placeholder="Size" list="sizes"></input>
            <input autoComplete="off" onChange={this.update_item} type="number" value={item.quantity} className="form-control item" min="1" name="quantity" placeholder="Quantity/Pairs" required />
            <input autoComplete="off" onChange={this.update_item} type="number" value={item.selling_price} className="form-control item " min="0" name="selling_price" placeholder="Price" required />
            <div className="input-group-postpend">
                {item.deleteable ? (<span onClick={this.delete_item} ><i className="fa fa-times-circle add-btn"></i></span>) : (<span onClick={this.add_item} id="add-item" ><i className="fa fa-plus-circle add-btn"></i></span>)}
            </div>
        </div>)
    })) : ("");

    return (
        <div className="container">
                    {/* {errors} */}

            <Menu></Menu>
            <div className="d-flex justify-content-center h-100">
                <div className="card float-left">
                    <div className="card-header">
                        <h3>Generate Reciept</h3>
                    </div>
                    <div className="card-body">
                        <form onSubmit={this.get_pdf} id="login_form">
                            <div className="input-group form-group">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fas fa-user"></i></span>
                                </div>
                                <input onChange={this.handleChange} type="text" value={this.state.fname} className="form-control" id="fname" name="fname" placeholder="First Name" required />
                            </div>
                            <div className="input-group form-group">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fas fa-user"></i></span>
                                </div>
                                <input onChange={this.handleChange} type="text" className="form-control" value={this.state.lname} id="lname" name="lname" placeholder="Last Name" required />
                            </div>
                            <div className="input-group form-group">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fa fa-mobile"></i></span>
                                </div>
                                <input onChange={this.handleChange} type="tel" value={this.state.mobile} className="form-control" id="mobile" placeholder="Mobile Number" name="mobile" pattern="[0-9]{10}" required />
                            </div>
                            <div className="input-group form-group">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fa fa-credit-card"></i></span>
                                </div>
                                <select onChange={this.handleChange} value={this.state.payment_method} id="payment" name="payment_method" className="form-control">
                                  <option> None</option>
                                    {payments}
                                </select>
                            </div>
                            <div id="items-cart">
                                {items}
                                <div>
                                </div>
                            </div>

                            <div className="input-group form-group float-right price-tag">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fa fa-rupee"></i></span>
                                </div>
                                <input type="text" className="form-control" value={this.state.amount_payable} readOnly id="amount" />
                            </div>

                            <div className="input-group form-group float-right price-tag">
                                <div className="input-group-prepend">
                                    <span className="input-group-text"><i className="fa fa-percent"></i></span>
                                </div>
                                <input type="number" onChange={this.handleChange} name="discount_given" value={this.state.discount_given} className="form-control" min="0" max="100" />
                            </div>

                            <div className="form-group">
                                <input type="submit" value="Submit" className="btn-lg login_btn" />
                            </div>

                        </form>
                        <datalist id="sizes">
                            {sizes}
                        </datalist>
                    </div>
                </div>
            </div>
            {/* <div aria-live="polite" aria-atomic="true" style={{position:"relative",minHeight: "200px"}}>
  <div style={{position: "absolute", top:0, right:0}}></div>
  {errors}
  </div> */}







        </div>
    )
}
}
export default Home;
