import React,{Component} from 'react'
import { MDBDataTableV5 } from 'mdbreact';
import Menu from './menu.component'
import '../css/sale.css';
import axios from 'axios';


class Customer extends Component
{
constructor(props)
{
    super(props);
    if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
      this.props.history.push('/')
  }
    this.state={
        datatable:{
            columns:[
                {
                    label: 'FirstName',
                    field: 'fname',
                    width: 150,
                  },
                  {
                    label: 'LastName',
                    field: 'lname',
                    width: 270,
                  },
                  {
                    label: 'Mobile',
                    field: 'mobile',
                    width: 270,
                  },
                  {
                    label: 'Amount Paid',
                    field: 'amount_payable',
                    width: 200,
                  },
                  {
                    label: 'Discount Provided',
                    field: 'total_discount',
                    sort: 'disabled',
                    width: 150,
                  },
                  {
                    label: 'Payment Type',
                    field: 'payment_method',
                    width: 270,
                  },
                  {
                    label: 'Date',
                    field: 'date',
                    width: 270,
                  },
                  {
                    label: 'Reciept',
                    field: 'receipt_pdf',
                    width: 270,
                  }
            ],
            rows: [
            ],
        }
    }
    this.payments="";
    var headers = {
      'Authorization': 'Token '+sessionStorage.getItem('token')
    }
    axios.get('https://shield.pythonanywhere.com/payments/payments_today/',{
      headers: headers
    })
    .then(response =>{
      var payments=response.data.Data.amount_wise_payment_list.map(payment=>{
        return(<tr> <th style={{color:"green"}}>{payment.name}</th><td>{payment.amount}</td></tr>)
      })
      var total_amount=response.data.Data.total_amount_paid_today
      this.setState({payments:payments,total_amount:total_amount})
    })
    }



    update_rows=(response)=>{
      console.log(response)
      let columns=this.state.datatable.columns
      let rows=this.state.datatable.rows
      console.log(columns);
      console.log(rows);
      response.Data.results.forEach(element=>{
        var row={'fname':element.customer.fname,'lname':element.customer.lname,'mobile':element.customer.mobile,'amount_payable':element.amount_payable,'total_discount':element.total_discount,'payment_method':element.payment_method.name,'date':element.customer.date,'receipt_pdf':element.receipt_pdf?(<a href={element.receipt_pdf} target="blank">click</a>):("")}
        console.log(row);
        rows.push(row)
      }
        )
      console.log(columns);
      console.log(rows);
      let datatable={columns:columns,rows:rows}
      this.setState({datatable:datatable})
  } 
    get_next=(response)=>{

      console.log(response)
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }

        axios.get(response,{headers:headers})
        .then(result=>{
          // console.log(result.data.data.results)
          this.update_rows(result.data);
          if(result.data.Data.links.next != null)
          {
            this.get_next(result.data.Data.links.next)
          }
        })

    }

    componentDidMount=()=>{
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }
      axios.get('https://shield.pythonanywhere.com/receipts/',{
        headers: headers
      })
      .then(response =>{
        // console.log(response.)
        this.update_rows(response.data);
        this.get_next(response.data.Data.links.next)
      })

      // axios.get('http://127.0.0.1:8000/payments/payments_today/',{
      //   headers: headers
      // })
      // .then(response =>{

      //   console.log(response)
      //   const payments=response.data.Data.amount_wise_payment_list.map(payment=>{
      //     return(<tr> <th style={{color:"green"}}>{payment.name}</th><td>{payment.amount}</td></tr>)
      //   })
      //   console.log(payments)
      //   // this.update_rows(response.data);
      //   // this.get_next(response.data.Data.links.next)
      // })

    }

render()
{
    return(
      <div> 
        <Menu/>
        <div style={{backgroundColor:"black",fontSize:"15px",color:"green"}}>
        <MDBDataTableV5 className="table-responsive" style={{backgroundColor:"black",fontSize:"20px",color:"white"}} hover entriesOptions={[10, 20, 25]} entries={10} pagesAmount={4} data={this.state.datatable} searchTop searchBottom={false} />
        </div>
        <div style={{textAlign:"left",backgroundColor:"black",fontSize:"20px",color:"white"}}>
        <table  style={{width:"100%"}}>
            <tr style={{color:"red"}}>
                <th>Payment Type</th>
                <th>Amount Paid</th>
            </tr>
  {/* <tr>
    <th style={{color:"green"}}>Cash</th>
    <td>15000</td>
  </tr>
  <tr>
    <th style={{color:"green"}}>Google Pay</th>
    <td>5000</td>
  </tr>
  <tr>
    <th style={{color:"green"}}>Paytm</th>
    <td>1000</td>
  </tr>
  <tr>
    <th style={{color:"green"}}>PhonePay</th>
    <td>150</td>
  </tr> */}
  {console.log(this.payments)}
  {this.state.payments}
  <tr>
    <th style={{color:"blue"}}>Total</th>
    <td style={{color:"blue"}}>{this.state.total_amount}</td>
  </tr>
</table>
</div>

      </div>

    )

}
}
export default Customer;
