import React,{Component} from 'react';
import '../css/home.css';

class Home extends Component
{
constructor(props)
{
    super(props)
    this.state={
        base_item:
        {
            "id":1,
            "name":"",
            "quantity":"",
            "price":"",
            "discount":"",
            "payable":"",
        },
        items:[
        ],
        count:1
    }

}

calculate=(e)=>{
    let id=e.target.getAttribute('id');
    const items=this.state.items.length?(this.state.items.map(item=>{ 
        if(item.id == id)
        {
        var value=(item.discount*item.price)/(100.0)
        item["payable"]=value;
        console.log(item);
        return(item);
        }
        else
        {
        return(item);
        }
        })):([])
        this.setState({
            items:items
        })
        }


add_item=()=>{
    let item={
        "id":this.state.count+1,
        "name":'',
        "quantity":'',
        "price":'',
        "discount":'',
        "payable":'',
    }
    let items=[...this.state.items,item];
    this.setState({
      items: items,
      count: this.state.count+1
    });
  };

delete_item=(e)=>{
    let id=e.target.getAttribute('value');
    let items=this.state.items.filter(item=>{
      return parseInt(item.id)!==parseInt(id);
    })
    this.setState({
      items: items
    })
  }

update_item=(e)=>{
let name=e.target.name
let id=e.target.getAttribute('id');
if(id == 1)
{
this.setState({
    base_item:{
        "id":this.state.base_item.id,
        "name":this.state.base_item.name,
        "quantity":this.state.base_item.quantity,
        "discount":this.state.base_item.discount,
        "payable":this.state.base_item.payable
    }
})
}
const items=this.state.items.length?(this.state.items.map(item=>{ 
if(item.id == id)
{
    console.log('matched')
item[name]=e.target.value;
console.log(item);
return(item);
}
else
{
return(item);
}
})):([])
this.setState({
    items:items
})
}


render()
{


    const items=this.state.items.length?(this.state.items.map( item => { return (
        <div key={item.id} className="input-group form-group items-list">
        <div className="input-group-prepend">
            <span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
        </div>
