import React,{Component} from 'react';
import { Link, NavLink, withRouter, Router } from 'react-router-dom';
import '../css/home.css';
import Menu from './menu.component'
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";
import axios from 'axios';

class Home extends Component
{
constructor(props)
{
super(props)
if(sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined')
{
this.props.history.push('/')
}
this.state={
fname:"",
lname:"",
mobile:"",
items:[
{
"id":1,
"item":"",
"brand":"",
"brands":[],
"quantity":"",
"selling_price":0,
"deleteable":false
}
],
discount_given:0,
payment_method:'',
total_amount:0,
total_discount:0,
amount_payable:0,
payment_options:[],
categories:[],
brands:[]
}
}

get_pdf=(e)=> {
e.preventDefault();
// console.log(this.state);
//console.log(document.getElementById('amount').value)
//console.log(this.state.items);
this.state.items.forEach(item=>{
    let item_in=this.state.categories.filter(category=>{
        if(item.item==category.name)
        {
            return category.id
        }        })
        item.item=item_in[0].id

})

this.state.items.forEach(item=>{
    let items=item.brands.filter(brand=>{
        //console.log(brand)
        if(brand.name==item.brand)
        {
            return brand.id
        }        })
        item.brand=items[0].id
})
const selectedIndex = document.getElementById('payment').options.selectedIndex;
let id=(document.getElementById('payment').options[selectedIndex].getAttribute('id'));
var data=this.state
data.payment_method=id
data.amount_payable=parseFloat(document.getElementById('amount').value)
data.total_discount=data.total_amount-data.amount_payable
// console.log(data);
// this.setState({payment_method:id})
// console.log(this.state);

// const data={"fname":this.state.fname,"lname":this.state.lname,"mobile":this.state.mobile}


