handleChange=(e)=>{
var data=e.target.value;
this.setState({
[e.target.name]:data
})
}


add_item=()=>{
let item={
"id":this.state.items.length+1,
"item":'',
"brand":'',
"brands":[],
"quantity":'',
"selling_price":0,
"deleteable":true,
}
let items=[...this.state.items,item];
this.setState({
items: items
});
};


delete_item=(e)=>{
let id=e.target.getAttribute('value');
let items=this.state.items.filter(item=>{
return parseInt(item.id)!==parseInt(id);
})
this.setState({
items: items
})
}


getBrands=(id)=>{
    const brands=this.state.categories.filter(category=>{ 
        return parseInt(category.id) == parseInt(id)
    })
    return(brands[0].brands)
}




update_item=(e)=>{
let name=e.target.name
let id=e.target.getAttribute('id');
if(name === 'item')
{
    const selectedIndex = e.target.options.selectedIndex;
    id=(e.target.options[selectedIndex].getAttribute('id'));
    var brands=this.getBrands(id);
}
id=e.target.getAttribute('id');

const items=this.state.items.length?(this.state.items.map(item=>{ 
if(item.id == id)
{
    if(name === 'item')
    {
        let arr=[]
        arr.push({"id":0,"name":"None"})
        brands.forEach(element=>{
            arr.push(element)
        })
        item['brands']=arr
    }
item[name]=e.target.value;
return(item);
}
else
{
return(item);
}
})):([])
//console.log(items)
this.setState({
items:items
})


this.setState({
total_amount:this.find_amount()
})
}

find_amount=()=>{
var amount=0;
this.state.items.forEach((item)=>{
    let price=item.selling_price
    let quantity=item.quantity
if(price == '')
{
    price=0;
}
if(quantity == '')
{
    quantity=1;
}
amount =parseInt(amount)+parseInt(price)*parseInt(quantity)
});
this.calculate(amount);
return amount;
}

calculate=(amount)=>{
let discount=this.state.discount_given;
if(discount == '')
{
    discount=0;
}
let reduced=(discount*amount/100)
let payable=amount-reduced;
document.getElementById('amount').value=payable;
}
componentDidUpdate=()=>{
    this.find_amount();
}



