import React,{Component} from 'react'
import '../css/menu.css';
import { NavLink } from 'react-router-dom';


class Menu extends Component
{
constructor(props)
{
    super(props);
    this.state={
        width:"0%"
    }
}
openNav=()=>{
    console.log("hello world")
    this.setState({
        width:"100%"
    })
}
closeNav=()=>{
    console.log("hello world")
    this.setState({
        width:"0%"
    })
}

render()
{
    return(
        <div style={{float:"left"}}>
            <div style={{width:this.state.width}} id="myNav" class="overlay">
  <a href="#" class="closebtn" onClick={this.closeNav}>&times;</a>
  <div class="overlay-content">
    <NavLink to="/home">Home</NavLink>
    <a href="/customer">Customers</a>
    <a href="/sale">Today's Sale</a>
    <a href="#">Contact</a>
  </div>
</div>
<span style={{fontSize:"40px",cursor:"pointer",color:"white"}} onClick={this.openNav}>&#9776;</span>
 </div>
    )
}
}
export default(Menu)
