import React, { Component } from 'react';
import CanvasJSReact from '../canvas/canvasjs.react';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class Graph extends Component {
	constructor(props)
	{
		super(props)
		this.state={
			dataPoints:[
				{ x: '01-01-2020', y: 64 },
				{ x: '02-01-2020', y: 64 },
				{ x: '03-01-2020', y: 59},
				{ x: '04-01-2020', y: 64 },
				{ x: '05-01-2020', y: 61 },
				{ x: '06-01-2020', y: 64 },
				{ x: '07-01-2020', y: 62 },
				{ x: '08-01-2020', y: 64 },
				{ x: '09-01-2020', y: 60 },
				{ x: '10-01-2020', y: 58 },
				{ x: '11-01-2020', y: 59 },
				{ x: '12-01-2020', y: 64 },
				{ x: '13-01-2020', y: 59},
				{ x: '14-01-2020', y: 64 },
				{ x: '15-01-2020', y: 61 },
				{ x: '16-01-2020', y: 64 },
				{ x: '17-01-2020', y: 62 },
				{ x: '18-01-2020', y: 64 },
				{ x: '19-01-2020', y: 60 },
				{ x: '20-01-2020', y: 58 },
				{ x: '21-01-2020', y: 59 },
				{ x: '22-01-2020', y: 64 },
				{ x: '23-01-2020', y: 59},
				{ x: '24-01-2020', y: 64 },
				{ x: '25-01-2020', y: 61 },
				{ x: '26-01-2020', y: 64 },
				{ x: '27-01-2020', y: 62 },
				{ x: '28-01-2020', y: 64 },
				{ x: '29-01-2020', y: 60 },
				{ x: '30-01-2020', y: 58 },
				{ x: '31-01-2020', y: 59 }
			]

		}
	}
	render() {
		const options = {
			animationEnabled: true,
			exportEnabled: true,
			theme: "light1", // "light1", "dark1", "dark2"
			backgroundColor:"rgb(0,0,0)",
			title:{
				text: "Sale Datewise for last month",
				fontColor:'rgb(255,255,255)'
			},
			axisY: {
				title: "Sales(in Rs)",
				fontColor:'rgb(255,255,255)',
				includeZero: false,
				labelFontColor:"rgb(255,255,255)"
			},
			axisX: {
				title: "Dates",
				fontColor:'rgb(255,255,255)',
				interval: 2,
				labelFontColor:"rgb(255,255,255)"
			},
			height:1000,
			data: [{
				type: "line",
				toolTipContent: "Date {x}: {y}%",
				dataPoints: this.state.dataPoints
			}]
		}


		return (
		<div>
			<h1>React Line Chart</h1>
			{/* <div style={{overflow:"scroll"}}> */}
			<div 
  style={{overflowX:"scroll"}}>
    <div class="chart" id="placeholder1">
	<CanvasJSChart options = {options} />
     </div>
</div>


			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		{/* </div> */}
			</div>

		);
	}
}

export default Graph; 


