import React,{Component} from 'react'
import { MDBDataTableV5 } from 'mdbreact';
import Menu from './menu.component'
import axios from 'axios';

class Items extends Component
{
constructor(props)
{
    super(props);
    if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
        this.props.history.push('/')
    }
    this.state={
        datatable:{
            columns:[
                {
                    label: 'Item',
                    field: 'item',
                    width: 150,
                  },
                  {
                    label: 'BrandName',
                    field: 'name',
                    width: 270,
                  },
                  {
                    label: 'Quantity',
                    field: 'quantity',
                    width: 200,
                  },
                  {
                    label: 'Price',
                    field: 'cost_price',
                    width: 200,
                  },
                  {
                    label: 'Discount',
                    field: 'intitial_discount',
                    width: 200,
                  },
                  {
                    label: 'Payable',
                    field: 'actual_cost_price',
                    width: 200,
                  }
            ],
            rows: [],
        }
    }

    }

    componentDidMount=()=>{
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }
      axios.get('https://shield.pythonanywhere.com/brands/',{
        headers: headers
      })
      .then(response =>{
        var items=response.data.Data.map(item=>{
            return({'item':item.item.name,'name':item.name,'quantity':item.quantity,'cost_price':item.cost_price,'intitial_discount':item.initial_discount,'actual_cost_price':item.actual_cost_price,})
          })
      let columns=this.state.datatable.columns
      let datatable={columns:columns,rows:items}
      this.setState({datatable:datatable})
          console.log(items)

      })

    }

render()
{



    return(
      <div>
           <Menu/>
        <div style={{backgroundColor:"black",fontSize:"15px",color:"green"}}>
        <MDBDataTableV5 className="table-responsive" style={{textAlign:"left",backgroundColor:"black",fontSize:"20px",color:"white"}} hover entriesOptions={[10, 20, 25]} entries={10} pagesAmount={4} data={this.state.datatable} searchTop searchBottom={false} />
        </div>
      </div>

    )

}
}
export default Items;
