import React, { Component } from 'react';
import { Link, NavLink, withRouter, Router } from 'react-router-dom';
import '../css/home.css';
import Menu from './menu.component'
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";
import axios from 'axios';

class Home extends Component {
    constructor(props) {
        super(props)
        if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
            this.props.history.push('/')
        }
        this.state = {
            fname: "",
            lname: "",
            mobile: "",
            payment_method: "",
            items: [
                {
                    "id": 1,
                    "item": "",
                    "size":"",
                    "quantity": 0,
                    "selling_price": 0,
                    "deleteable": false
                }
            ],
            discount_given: 0,
            total_amount:0,
            total_discount: 0,
            amount_payable: 0,
            payment_options: [],
            categories: [],
            sizes: []
        }
    }

    get_pdf = (e) => {
        e.preventDefault();
        this.state.items.forEach(item => {
            let item_in = this.state.categories.filter(category => {
                if (item.item == category.name) {
                    return category.id
                }
            })
            item.item = item_in[0].id

        })

        this.state.items.forEach(item => {
            let items = item.brands.filter(brand => {
                if (brand.name == item.brand) {
                    return brand.id
                }
            })
            item.brand = items[0].id
        })
        const selectedIndex = document.getElementById('payment').options.selectedIndex;
        let id = (document.getElementById('payment').options[selectedIndex].getAttribute('id'));
        var data = this.state
        data.payment_method = id
        data.amount_payable = parseFloat(document.getElementById('amount').value)
        data.total_discount = data.total_amount - data.amount_payable



        const headers = {
            'Authorization': 'Token ' + sessionStorage.getItem('token')
        }
        axios.post('http://127.0.0.1:8000/receipts/', data, {
            headers: headers,
        })
            .then(response => {
                //   console.log(response)
                // window.location.href(response.data.Data.receipt_link)
                // const file = new Blob(
                // [response.data], 
                // {type: 'application/pdf'});
                // const fileURL = URL.createObjectURL(file);
                // console.log(fileURL)
                window.open("http://127.0.0.1:8000" + response.data.Data.receipt_link);
            })
            .catch(error => {
            });
    }




    handleChange = (e) => {
        var data = e.target.value;
        this.setState({
            [e.target.name]: data
        })
    }


    add_item = () => {
        let item = {
