import React,{Component} from 'react';
import '../css/home.css';
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";

class Home extends Component
{
constructor(props)
{
    super(props)
    this.state={
        base_item:
        {
            "id":1,
            "name":'',
            "quantity":"",
            "price":"",
            "discount":"",
            "payable":"",
        },
        items:[],
        count:1,
        total_amount:0
    }

}



get_pdf=()=> {
    const ref = React.createRef();
    function App() {
      return (
        <div className="App">
          <Pdf targetRef={ref} filename="code-example.pdf">
            {({ toPdf }) => {()=>{toPdf}}}
          </Pdf>
          <div ref={ref}>
            <h1>Hello CodeSandbox</h1>
            <h2>Start editing to see some magic happen!</h2>
          </div>
        </div>
      );
    }
  const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
}






find_amount=()=>{
    var amount=0;
    this.state.items.forEach((item)=>{
        amount +=item.payable
    })
    console.log(amount);
    return amount+this.state.base_item.payable;
}

calculate=(e)=>{
    e.preventDefault();
    let id=e.target.getAttribute('id');
    if(id == 1)
    {
    let item=this.state.base_item;

    var value=this.state.base_item.price-(this.state.base_item.price*this.state.base_item.discount)/(100.0)
    item["payable"]=value;
this.setState({
    base_item:item,
    total_amount: this.find_amount()
})
console.log(this.state);
    }
else
{
    var value;
    const items=this.state.items.length?(this.state.items.map(item=>{ 
        if(item.id == id)
        {
        value=item.price-(item.discount*item.price)/(100.0)
        item["payable"]=value;
        return(item);
        }
        else
        {
        return(item);
        }
        })):([])
        this.setState({
            items:items,
            total_amount:this.find_amount()
        })
        console.log(this.state);
        }
    }

add_item=()=>{
    let item={
        "id":this.state.count+1,
        "name":'',
        "quantity":'',
        "price":'',
        "discount":'',
        "payable":'',
    }
    let items=[...this.state.items,item];
    this.setState({
      items: items,
      count: this.state.count+1
    });
  };

delete_item=(e)=>{
    let id=e.target.getAttribute('value');
    let items=this.state.items.filter(item=>{
      return parseInt(item.id)!==parseInt(id);
    })
    this.setState({
      items: items
    })
  }

update_item=(e)=>{
let name=e.target.name
let id=e.target.getAttribute('id');
if(id == 1)
{
    let item=this.state.base_item;
    item[name]=e.target.value;
    var amount=0;
this.setState({
    base_item:item
})
}
else
{
const items=this.state.items.length?(this.state.items.map(item=>{ 
if(item.id == id)
{

item[name]=e.target.value;

return(item);
}
else
{
return(item);
}
})):([])
this.setState({
    items:items
})
}
}


render()
{


    const items=this.state.items.length?(this.state.items.map( item => { return (
        <div key={item.id} className="input-group form-group items-list">
        <div className="input-group-prepend">
            <span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
        </div>
        <input onChange={this.update_item} id={item.id} type="text" list="items" value={item.name} className="form-control item" name="name" placeholder="Item" required/>
        <input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.quantity} className="form-control item" min="1" name="quantity" placeholder="Quantity/Pairs" required/>
        <input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.price} className="form-control item" min="0" name="price" placeholder="Price" required/>
        <input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.discount} className="form-control item" name="discount" placeholder="Discount" min="0" required/>
        <input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.payable} className="form-control item" placeholder="Payble" name="payable" readOnly required/>
        <input autoComplete="off" onClick={this.calculate} id={item.id} type="submit" value="calculate" className="btn-sm float-right calculate_btn"/>
        <div className="input-group-postpend">
            <span><i value={item.id} onClick={this.delete_item} className="fa fa-times-circle add-btn"></i></span>
        </div>
</div>

)})):("");   

    return(

        <div className="container">
	<div className="d-flex justify-content-center h-100">
		<div className="card float-left">
			<div className="card-header">
				<h3>Generate Reciept</h3>
			</div>
			<div className="card-body">
				<form id="login_form">

					<div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fas fa-user"></i></span>
						</div>
						<input type="text" className="form-control" id="firstname" placeholder="First Name" required/>	
					</div>

                    <div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fas fa-user"></i></span>
                        </div>
                        <input type="text" className="form-control" id="lastname" placeholder="Last Name" required/>
					</div>

                    <div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fa fa-mobile"></i></span>
						</div>
						<input type="tel" className="form-control" id="mobile" placeholder="Mobile Number" pattern="[0-9]{10}"required/>
                    </div>

                    <div className="input-group form-group">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fa fa-credit-card"></i></span>
                        </div>
                        <select className="form-control">
                            <option>Cash</option>
                            <option>Credit Card</option>
                            <option>Debit Card</option>
                            <option>Google Pay</option>
                            <option>Paytm</option>
                            <option>PhonePay</option>
                        </select>
                    </div>


                    <div id="items-cart">
                        <div key="1" className="input-group form-group items-list">
                            <div className="input-group-prepend">
                                <span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
                            </div>
                            <input onChange={this.update_item} value={this.state.base_item.name} type="text" list="items" className="form-control item" id="1" name="name" placeholder="Item" required/>
                            <input onChange={this.update_item} value={this.state.base_item.quantity} autoComplete="off" onChange={this.update_item} type="number" name="quantity" className="form-control item" id="1" min="1" id="1" placeholder="Quantity/Pairs" required/>
                            <input onChange={this.update_item} value={this.state.base_item.price} autoComplete="off"  onChange={this.update_item} type="number" name="price" className="form-control item" id="1" min="0" placeholder="Price" required/>
                            <input onChange={this.update_item} value={this.state.base_item.discount} autoComplete="off"  onChange={this.update_item} type="number" name="discount" className="form-control item" id="1" placeholder="Discount" min="0" required/>
                            <input onChange={this.update_item} value={this.state.base_item.payable} autoComplete="off"  onChange={this.update_item}type="number" name="payable" className="form-control item" id="1" placeholder="Payble" readOnly required/>
                            <input onClick={this.calculate} id="1" type="submit" value="calculate" className="btn-sm float-right calculate_btn"/>
                            <div className="input-group-postpend">
                                <span onClick={this.add_item} id="add-item" ><i className="fa fa-plus-circle add-btn"></i></span>
                            </div>
                    </div>
                    {items}
                    <div>

                    </div>
                    </div>




                    <div className="input-group form-group float-right price-tag">
						<div className="input-group-prepend">
							<span className="input-group-text"><i className="fa fa-rupee"></i></span>
                        </div>
                        <input type="text" className="form-control" readOnly value={this.state.total_amount} id="amount"/>
					</div>

					<div className="form-group">
						<input type="submit" onClick={this.get_pdf} value="Submit" className="btn-lg login_btn"/>
                    </div>

                </form>
                <datalist id="items">
                            <option>Socks</option>
                            <option>Shirts</option>
                            <option>T shirts</option>
                            <option>Gloves</option>
                </datalist>
			</div>
		</div>
	</div>
</div>
    )
}
}
export default Home;
