import React,{Component} from 'react'
import '../css/menu.css';


class Menu extends Component
{
constructor(props)
{
    super(props);
    this.state={
        width:0
    }
}
openNav=()=>{
    this.setState({
        width:100
    })
}

render()
{
    return(
        <div>
            <div style={{width:this.state.width}} id="myNav" class="overlay">
  <a href="javascript:void(0)" class="closebtn" onclick="closeNav()">&times;</a>
  <div class="overlay-content">
    <a href="#">About</a>
    <a href="#">Services</a>
    <a href="#">Clients</a>
    <a href="#">Contact</a>
  </div>
  <span className="nav_button" onclick={this.openNav()}>&#9776; open</span>
</div>
</div>
    )
}
}
export default(Menu)
