import React, { Component } from 'react';
import * as d3 from 'd3'
/*
 * inspired by shirley wu's blog post 
 * https://medium.com/@sxywu/on-d3-react-and-a-little-bit-of-flux-88a226f328f3#.e46japl3o
 * still working on axis
 */
function makeRandomNumber(max, min) {
  return Math.floor(Math.random() * (max - min)) + min;
}

// d3 update part
var visualization = {
  enter: (selection, xScale, yScale) => {
    var yZeroPoint = yScale(0);
    selection.select('rect.bar').attr('x', (d) => xScale(d.x))
             .attr('y', yZeroPoint)
             .attr('height', 0)
             .attr('width', xScale.rangeBand())
             .transition()
             .duration(300)
             .attr('y', (d) => yScale(d.y))
             .attr('height', (d) => yZeroPoint - yScale(d.y));

    selection.select('text.index').attr('x', (d) => xScale.rangeBand() * 2 / 5 + xScale(d.x))
             .attr('y', yZeroPoint + 20);

    selection.select('text.value').attr('x', (d) => xScale.rangeBand() * 1 / 8 + xScale(d.x))
             .attr('y', yZeroPoint + 20)
             .transition()
             .duration(300)
             .attr('y', (d) => yScale(d.y) - 10);
  },
  update: (selection, xScale, yScale) => {
    var yZeroPoint = yScale(0);
    selection.select('rect.bar').attr('x', (d) => xScale(d.x))
             .attr('y', yZeroPoint)
             .attr('height', 0)
             .attr('width', xScale.rangeBand())
             .transition()
             .duration(300)
             .attr('y', (d) => yScale(d.y))
             .attr('height', (d) => yZeroPoint - yScale(d.y));

    selection.select('text.index').attr('x', (d) => xScale.rangeBand() * 2 / 5 + xScale(d.x))
             .attr('y', yZeroPoint + 20);

    selection.select('text.value').attr('x', (d) => xScale.rangeBand() * 2 / 8 + xScale(d.x))
             .attr('y', yZeroPoint + 20)
             .transition()
             .duration(300)
             .attr('y', (d) => yScale(d.y) - 10);
  }
};

var tickVisualization = {
  enter: (selection, transX, transY, zeroPoint) => {
    selection
      .attr('transform', 'translate(' + transX + ',' + zeroPoint + ')')
      .transition()
      .attr('transform', 'translate(' + transX + ',' + transY + ')');
  }
}
// used in Tick component
var horizonLine = d3.svg.line()
.y((d) => d.y)
.x((d) => d.x);

var Graph = React.createClass({

  getInitialState() {
    return {
      data: []
    };
  },

  componentDidMount() {
    this.updateData();
    //setInterval(this.updateData, 3000);
  },

  updateData() {
    var dataLen = makeRandomNumber(5,10);
    var data = d3.range(dataLen).map((i) => {
      return {x: i, y: makeRandomNumber(1000, 10000)};
    });
    this.setState({data: data});
  },

  render () {
    var svgSize={width: 400, height:400}
    return (
      <BarChart svgSize={svgSize} data={this.state.data}/>
    );
  }
});

var BarChart = React.createClass({
  render() {
    var svgMargin = {top: 30, right: 20, bottom: 30, left:50}
    var xScale = d3.scale.ordinal()
                         .rangeBands([svgMargin.left ,this.props.svgSize.width - svgMargin.right],.2)
                         .domain(this.props.data.map((d) => d.x));
    var yScale = d3.scale.linear()
                         .range([this.props.svgSize.height - svgMargin.top, svgMargin.bottom])
                         .domain([0, d3.max(this.props.data, (d) => d.y)]);

    var bars = this.props.data.map((d) => {
      return (
        <Bar data={d} xScale={xScale} yScale={yScale} svgSize={this.props.svgSize} barWidth={10} />
        );
    })
    return (
      <svg width={this.props.svgSize.width} 
           height={this.props.svgSize.height}>
        <Axis scale={yScale}/>
        {bars}
      </svg>
    );
  }
});

var Tick = React.createClass({
  componentDidMount() {
    var horizonLineData = [{x: 0, y: 0}, {x: 400, y: 0}];
    this.d3Node = d3.select(React.findDOMNode(this));
    tickVisualization.enter(this.d3Node, this.props.transX, this.props.transY, this.props.zeroPoint);
    this.d3Node.select('path').datum(horizonLineData)
    .attr('d', horizonLine);
  },
  shouldComponentUpdate() {
    return true;
  },
  componentWillUpdate(nextProps) {
    var horizonLineData = [{x: 0, y: 0}, {x: 400, y: 0}];
    tickVisualization.enter(this.d3Node, nextProps.transX, nextProps.transY, nextProps.zeroPoint);
    this.d3Node.select('path').datum(horizonLineData)
    .attr('d', horizonLine);
  },
  format(d) {
    var formatNumber = d3.format('^5d')
    return '$' + formatNumber(d);
  },
  render() {
    return (
      <g className="tick">
        <text fill="#888">{this.format(this.props.tick)}</text>
        <path className="horizon"></path>
      </g>
    );
  }
});

var Axis = React.createClass({

  render() {
    var scale = this.props.scale.copy();

    var ticks = scale.ticks(4);
    var transX = -30;
    var tickComps = ticks.map((t) => {
      var transY = scale(t);
      var zeroPoint = scale(0);
      return (
        <Tick transX={transX} transY={transY} tick={t} zeroPoint={zeroPoint}/>
      );
    })
    return (
      <g className="y-axis" transform="translate(30,0)">
        {tickComps}
      </g>
    );
  }
});

var Bar = React.createClass({
  getInitialState() {
    return {
      barColor: 'steelblue',
      barClass: 'bar'
    };
  },
  componentDidMount() {
    this.d3Node = d3.select(React.findDOMNode(this));
    visualization.enter(this.d3Node.datum(this.props.data), this.props.xScale, this.props.yScale);
  },
  shouldComponentUpdate() {
    return true;
  },
  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      visualization.update(this.d3Node.datum(this.props.data), this.props.xScale, this.props.yScale);
    }
  },
  mouseOverHandler() {
    this.setState({barClass: 'bar-active'});
  },
  mouseOutHandler() {
    this.setState({barClass: 'bar'});
  },
  render() {
    var yZeroPoint = this.props.yScale(0);
    return (
      <g>
        <rect className={this.state.barClass}
              onMouseOver = {this.mouseOverHandler}
              onMouseOut = {this.mouseOutHandler}>
        </rect>
        <text className="index">{this.props.data.x + 1}</text>
        <text className="value">{this.props.data.y}</text>
      </g>
    );
  }
});

// React.render(<Graph />, document.getElementById("main"));
export default Graph
