import React,{Component} from 'react';
import { Link, NavLink, withRouter, Router } from 'react-router-dom';
import '../css/home.css';
import Menu from './menu.component'
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";
import axios from 'axios';

class Home extends Component
{
constructor(props)
{

    super(props)
    console.log(sessionStorage.getItem('token'))
    console.log(sessionStorage.getItem('username'))

    if(sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined')
    {
        this.props.history.push('/')
    }
    this.state={
        firstname:"",
        lastname:"",
        mobile:"",
        payment:"",
        items:[
            {
            "id":1,
            "category":"",
            "brand":"",
            "quantity":"",
            "price":"",
            "deleteable":false
            }
        ],
        total_amount:0
    }

}



get_pdf=(e)=> {
    e.preventDefault();
    axios("http://127.0.0.1:8000/reciept/", {
        method: 'POST',
        responseType: 'blob'
    })
    .then(response => {
        const file = new Blob(
          [response.data], 
          {type: 'application/pdf'});
        const fileURL = URL.createObjectURL(file);
        window.open(fileURL);
    })
    .catch(error => {
        console.log(error);
    });
}



handleChange=(e)=>{
	var data=e.target.value;
	this.setState({
	  [e.target.name]:data
	})
  }



find_amount=()=>{

    var amount=0;
    this.state.items.forEach((item)=>{
        if(item.price!='')
        {
        amount =parseInt(amount)+parseInt(item.price)*parseInt(item.quantity)
        console.log(amount);
        }
    })
    console.log(amount);
    return amount+parseInt(this.state.base_item.price)*parseInt(this.state.base_item.quantity);
}

calculate=(e)=>{
    let discount=e.target.value;
    let reduced=(discount*this.find_amount())/100
    let payable=this.find_amount()-reduced;
    this.setState({
        total_amount:payable
    })
    }

add_item=()=>{
    let item={
        "id":this.state.items.length+1,
        "name":'',
        "quantity":'',
        "price":'',
        "discount":'',
        "payable":'',
        "deleteable":true,
    }
    let items=[...this.state.items,item];
    this.setState({
      items: items
    });
  };

delete_item=(e)=>{
    let id=e.target.getAttribute('value');
    let items=this.state.items.filter(item=>{
      return parseInt(item.id)!==parseInt(id);
    })
    this.setState({
      items: items
    })
  }

update_item=(e)=>{
let name=e.target.name
let id=e.target.getAttribute('id');
if(id == 1)
{
    let item=this.state.base_item;
    item[name]=e.target.value;
    var amount=0;
this.setState({
    base_item:item
})
}
else
{
const items=this.state.items.length?(this.state.items.map(item=>{ 
if(item.id == id)
{

item[name]=e.target.value;

return(item);
}
else
{
return(item);
}
})):([])
this.setState({
    items:items
})
}

    console.log('caught');
    this.setState({
        total_amount:this.find_amount()
    })

}


render()
{
const items=this.state.items.length?(this.state.items.map( item => { return (
<div key={item.id} className="input-group form-group items-list">
<div className="input-group-prepend">
<span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
</div>
<input onChange={this.update_item} id={item.id} type="text" list="items" value={item.category} className="form-control item" name="name" placeholder="Item" required/>
