import React,{Component} from 'react'
import { MDBDataTableV5 } from 'mdbreact';
import Menu from './menu.component'
import axios from 'axios';

class Items extends Component
{
constructor(props)
{
    super(props);
    this.state={
        datatable:{
            columns:[
                {
                    label: 'Item',
                    field: 'item',
                    width: 150,
                  },
                  {
                    label: 'BrandName',
                    field: 'name',
                    width: 270,
                  },
                  {
                    label: 'Quantity',
                    field: 'quantity',
                    width: 200,
                  },
                  {
                    label: 'Price',
                    field: 'cost_price',
                    width: 200,
                  },
                  {
                    label: 'Discount',
                    field: 'intitial_discount',
                    width: 200,
                  },
                  {
                    label: 'Payable',
                    field: 'actual_cost_price',
                    width: 200,
                  }
            ],
            rows: [],
        }
    }

    }

    componentDidMount=()=>{
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }
      axios.get('http://127.0.0.1:8000/brands/',{
        headers: headers
      })
      .then(response =>{
        var items=response.data.Data.map(item=>{
            return({'item':item.item.name,'name':item.name,'quantity':item.quantity,'cost_price':item.cost_price,'initial_discount':item.initial_discount,'actual_cost_price':item.actual_cost_price,})
          })
          console.log(items)
        //   var total_amount=response.data.Data.total_amount_paid_today
        //   this.setState({payments:payments,total_amount:total_amount})
      })

    }

render()
{



    return(
      <div>
           <Menu/>
        <div style={{backgroundColor:"black",fontSize:"15px",color:"green"}}>
        <MDBDataTableV5 className="table-responsive" style={{textAlign:"left",backgroundColor:"black",fontSize:"20px",color:"white"}} hover entriesOptions={[10, 20, 25]} entries={10} pagesAmount={4} data={this.state.datatable} searchTop searchBottom={false} />
        </div>
      </div>

    )

}
}
export default Items;
