import React,{Component} from 'react';
import { Link, NavLink, withRouter, Router } from 'react-router-dom';
import '../css/home.css';
import Menu from './menu.component'
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';
import MyDocument from './pdf.component';
import ReactDOM from 'react-dom';
import Pdf from "react-to-pdf";
import axios from 'axios';

class Home extends Component
{

constructor(props)
{
super(props)
console.log(sessionStorage.getItem('token'))
console.log(sessionStorage.getItem('username'))
if(sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined')
{
this.props.history.push('/')
}
this.state={
firstname:"",
lastname:"",
mobile:"",
items:[
{
"id":1,
"category":"",
"brand":"",
"quantity":1,
"price":0,
"deleteable":false
}
],
discount_given:0,

payment_options:["Cash","Credit Card","Debit Card","Google Pay","Paytm","PhonePay"],
categories:["Socks","Shirts","T-shirts","Gloves"]
}
this.data={
    total_amount:0,
    total_discount:0,
    amount_payable:0,
}
}

get_pdf=(e)=> {
e.preventDefault();
axios("http://127.0.0.1:8000/reciept/", {
method: 'POST',
responseType: 'blob'
})
.then(response => {
const file = new Blob(
[response.data], 
{type: 'application/pdf'});
const fileURL = URL.createObjectURL(file);
window.open(fileURL);
})
.catch(error => {
console.log(error);
});
}


handleChange=(e)=>{
var data=e.target.value;
this.setState({
[e.target.name]:data
})
}




add_item=()=>{
let item={
"id":this.state.items.length+1,
"category":'',
"brand":'',
"quantity":'',
"price":0,
"deleteable":true,
}
let items=[...this.state.items,item];
this.setState({
items: items
});
};

delete_item=(e)=>{
let id=e.target.getAttribute('value');
let items=this.state.items.filter(item=>{
return parseInt(item.id)!==parseInt(id);
})
this.setState({
items: items
})
}

update_item=(e)=>{
let name=e.target.name
let id=e.target.getAttribute('id');
let value=e.target.value
if(name==="quantity" && value==="")
{
value=1
}
if(name==="price" && value==="")
{
value=0
}
const items=this.state.items.length?(this.state.items.map(item=>{ 
if(item.id == id)
{
item[name]=value;
return(item);
}
else
{
return(item);
}
})):([])
this.setState({
items:items
})

}

find_amount=()=>{
var amount=0;
this.state.items.forEach((item)=>{
    console.log(item.price);
if(item.price=="")
{item.price=0}
amount =parseInt(amount)+parseInt(item.price)*parseInt(item.quantity)
console.log(amount)
});
console.log(amount)
this.data.total_amount=amount;
console.log(this.data);
return amount;
}

calculate=()=>{
let discount=this.state.discount_given;
let total_amount=this.state.total_amount
let reduced=(discount*total_amount/100)
this.data.discount=reduced;
let payable=total_amount-reduced;
this.data.payable=payable
return payable
}

componentDidUpdate=()=>{
    this.find_amount();
    this.calculate();
}
render()
{
const items=this.state.items.length?(this.state.items.map( item => { return (
<div key={item.id} className="input-group form-group items-list">
<div className="input-group-prepend">
<span className="input-group-text"><i className="fa fa-shopping-cart"></i></span>
</div>
<input onChange={this.update_item} id={item.id} type="text" list="items" value={item.category} className="form-control item" name="category" placeholder="Item" required/>
<input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.quantity} className="form-control item" min="1" name="quantity" placeholder="Quantity/Pairs" required/>
<input autoComplete="off" onChange={this.update_item} id={item.id} type="number" value={item.price} className="form-control item" min="0" name="price" placeholder="Price" required/>
<div className="input-group-postpend">
{item.deleteable?(<span><i value={item.id} onClick={this.delete_item} className="fa fa-times-circle add-btn"></i></span>):(<span onClick={this.add_item} id="add-item" ><i className="fa fa-plus-circle add-btn"></i></span>)}
</div>
</div>
)})):("");   

const payments=this.state.payment_options.length?(this.state.payment_options.map(option =>{
return(<option>{option}</option>)
})):("");

const categories=this.state.categories.length?(this.state.categories.map(category =>{
    return(<option>{category}</option>)
    })):("");

return(

<div className="container">

<Menu></Menu>

<div className="d-flex justify-content-center h-100">
<div className="card float-left">

<div className="card-header">
<h3>Generate Reciept</h3>
</div>

<div className="card-body">
