import React, { Component } from 'react';
import '../css/home.css';
import Menu from './menu.component'
import axios from 'axios';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class Home extends Component {
    constructor(props) {
        super(props)
     let date=new Date(); 
     console.log(date.getFullYear()+'-'+date.getMonth()+1+'-'+date.getDate())
        if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
            this.props.history.push('/')
        }
        this.state = {
            fname: "",
            lname: "",
            mobile: "",
            payment_method: "",
            birth_day: "",
            items: [
                {
                    "id": 1,
                    "item": "",
                    "brand":"",
                    "size":"",
                    "quantity": 1,
                    "selling_price": 0,
                    "deleteable": false
                }
            ],
            discount_given: 0,
            total_amount:0,
            total_discount: 0,
            amount_payable: 0,
            payment_options: [],
            categories: [],
            sizes: [],
            errors: [{status:200,error:"error",message:"error message"},{status:200,error:"error",message:"error message"}]
        }
        const headers = { 'Authorization': 'Token ' + sessionStorage.getItem('token') }

        axios.get('https://shield.pythonanywhere.com/payments/', { headers: headers })
            .then(response => {
                console.log(response)
                var errors=[]
                if(response.data.status != 404)
                {
                this.setState({ payment_options: response.data.Data })
                // console.log(this.state)
                }

            })
            .catch(err=>{
                let error={"status":"","error":err,"message":"Unknown error occured while getting payment options"}
                let errors = [...this.state.errors, error];
                this.setState({errors:errors})
            }
            )

        axios.get('https://shield.pythonanywhere.com/items/itemsBrand/', { headers: headers })
            .then(response => {
                console.log(response)
                if(response.data.status != 404)
                {

                    let categories=response.data.Data
                    // this.setState({ categories: [{'id':-1,'name':"none","brands":[]},...categories.slice(0)] })
                    this.setState({categories:categories})


                }

            })
            .catch(err=>{
                let error={"status":"","error":err,"message":"Unknown error occured while fetching Items and Brands options"}
               let  errors = [...this.state.errors, error];
                this.setState({errors:errors})
            }
            )

        axios.get('https://shield.pythonanywhere.com/sizes/', { headers: headers })
            .then(response => {

                this.setState({ sizes: response.data.Data })
            }) 
            let money=this.find_sum()
            this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})

        const items = this.state.items.length ? (this.state.items.map(item => {
            let brands = this.state.categories.filter(category => {

                if (item.item == category.name) {
                    return category
                }
            })
           })):("")
    }


    get_pdf = (e) => {
        e.preventDefault();
        var data=this.state
        this.state.payment_options.map(option=>{
            if(option.name === data.payment_method)
            {
                data.payment_method=option.id
            }
        })
        let date=this.state.birth_day
        data.birth_day=this.formatDate(date)
data.items.map(item=>{
    data.categories.map(category=>{
        if(item.item === category.name)
        {
            item.item=category.id
            category.brands.map(brand=>{
                    if(brand.name === item.brand)
                    {
                        item.brand=brand.id
                    }

            })
        }
    })
})
// console.log(data);
        const headers = {
            'Authorization': 'Token ' + sessionStorage.getItem('token')
        }
        axios.post('https://shield.pythonanywhere.com/receipts/', data, {
            headers: headers,
        })
            .then(response => {
                // console.log(response)
                window.open("https://shield.pythonanywhere.com" + response.data.Data.receipt_link);
            })
            .catch(error => {
                console.log(error)
            });
    }




    handleChange = (e) => {
        var data = e.target.value;
        var money=this.find_sum()
        this.setState({
            [e.target.name]: data
        },()=>{let money=this.find_sum();this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})})
    }

    formatDate=(date)=> {
        var d = new Date(date),
            month = '' + (d.getMonth() + 1),
            day = '' + d.getDate(),
            year = d.getFullYear();

        if (month.length < 2) 
            month = '0' + month;
        if (day.length < 2) 
            day = '0' + day;

        return [year, month, day].join('-');
    }

    onChange =(birth_day) => {
        this.setState({birth_day:birth_day})};


    add_item = () => {
        let item = {
            "id": this.state.items.length + 1,
            "item": "",
            "brand":"",
            "size":"",
            "quantity":1,
            "selling_price": 0,
            "deleteable": true,
        }
        let items = [...this.state.items, item];
        this.setState({
            items: items
        });
    };


    delete_item = (e) => {
        let key= e.currentTarget.parentNode.parentNode.getAttribute("data-key");
        // console.log(key)
        let items = this.state.items.filter(item => {
            return parseInt(item.id) !== parseInt(key);
        })

        this.setState({items: items},()=>{ let money=this.find_sum();this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})})
    }


    update_item =   (e) =>  {
       let key  =   e.currentTarget.parentNode.parentNode.parentNode.getAttribute("data-key");
    //    console.log(key)
       let name =   e.currentTarget.getAttribute("name")
       let value    =   e.currentTarget.value
       let price=0
       let quantity=0
       let actual_cost_price=0
       let found=false;
       if(value === "")
       {
           console.log(name)
           console.log(value)
           if(name != "size")
           {
           return;
           }
       }
       const items =  this.state.items.length?  (this.state.items.map(item => {
           if (parseInt(item.id) === parseInt(key))
           {
               item[name]=value;
               if(name=="brand")
               {
                   this.state.categories.map(category=>{
                       if(item.item === category.name)
                       {
                       console.log(category)
                       category.brands.map(brand=>{
                           if(brand.name === value)
                           {
                               item.selling_price=brand.actual_cost_price
                               found=true
                           }
                       })
                       }
                       if(found == false)
                       {
                           item.selling_price=0
                       }

                   })
               }
           }
           console.log(this.state)
           return(item)
       })):([])
       let money=this.find_sum()

       this.setState({total_amount:money[0],amount_payable:money[2],total_discount:money[1]})


    }



    getBrands = (id) => {
        const brands = this.state.categories.filter(category => {
            return parseInt(category.id) == parseInt(id)
        })
        return (brands[0].brands)
    }


    find_sum = (quantity,value) => {
        var money=new Array(3).fill(0)
        var discount=parseInt(this.state.discount_given?(this.state.discount_given):0)
        this.state.items.forEach((item) => {
            let price = parseInt(item.selling_price)
            let quantity = parseInt(item.quantity)
            money[0] = parseInt(money[0]) + parseInt(price) * parseInt(quantity)
        });
        money[1]=Math.round((discount*money[0])/100.0)
        money[2]=money[0]-money[1]
        return money;
    }

    calculate = (amount) => {
