import React,{Component} from 'react';
import BarChart from 'react-bar-chart';

const data = [
  {text: 'a', value: 1500}, 
  {text: 'b', value: 300},
  {text: 'c', value: 1500}, 
  {text: 'd', value: 300},
  {text: 'e', value: 1500}, 
  {text: 'f', value: 300},
  {text: 'g', value: 1500}, 
  {text: 'h', value: 300},
  {text: 'i', value: 1500}, 
  {text: 'j', value: 300},
  {text: 'k', value: 1500}, 
  {text: 'l', value: 300} 
];

const margin = {top: 20, right: 20, bottom: 30, left: 40};

class Graph extends Component
{
  constructor(props)
  {
    super(props)
    this.state=
    {
      "width":500
    }
    // return { width: 500 };
  }

  componentDidMount=() => {
    window.onresize = () => {
     this.setState({width: this.refs.root.offsetWidth}); 
    };
  }

  handleBarClick(element, id){ 
    console.log(`The bin ${element.text} with id ${id} was clicked`);
  }

  render() {
    return (
        <div ref='root'>
            <div style={{width: '100%'}}> 
                <BarChart ylabel='Quantity'
                  width={this.state.width}
                  height={500}
                  margin={margin}
                  data={data}
                  onBarClick={this.handleBarClick}/>
            </div>
        </div>
    );
  }
}
export default Graph;
