import React,{Component} from 'react'
import '../css/menu.css';


class Menu extends Component
{
constructor(props)
{
    super(props);
    this.state={
        width:"0%"
    }
}
openNav=()=>{
    console.log("hello world")
    this.setState({
        width:"100%"
    })
}
closeNav=()=>{
    console.log("hello world")
    this.setState({
        width:"0%"
    })
}

render()
{
    return(
        <div>
            <div style={{width:this.state.width}} id="myNav" class="overlay">
  <a href="javascript:void(0)" class="closebtn" onClick={this.closeNav}>&times;</a>
  <div class="overlay-content">
    <a href="#">About</a>
    <a href="#">Services</a>
    <a href="#">Clients</a>
    <a href="#">Contact</a>
  </div>
</div>
<span style={{fontSize:"40px",cursor:"pointer",color:"white"}} className='float' onClick={this.openNav}>&#9776; open</span>
 </div>
    )
}
}
export default(Menu)
