import React,{Component} from 'react'
import Chart from "react-google-charts";
import Menu from './menu.component';
import axios from 'axios';

class Graph extends Component
{
    constructor(props)
    {
		super(props)


        if (sessionStorage.getItem('token') === 'undefined' || sessionStorage.getItem('username') === 'undefined') {
            this.props.history.push('/')
        }
		this.state={
			data:['Sale', '']
		}
	}
	componentDidMount=()=>{
		var headers = {
			'Authorization': 'Token '+sessionStorage.getItem('token')
		  }
		  axios.get('https://shield.pythonanywhere.com/receipts/sales_for_graph/',{
			headers: headers
		  })
		  .then(response =>{
			  var data=[]
			  if(response.data.status != 404)
			  {
			  data.push(this.state.data)
			  console.log(data)
			  response.data.Data.date_wise_payment_list.map(sell=>{
				  let temp=[]
				  temp.push(sell.date)
				  temp.push(sell.amount)
				  data.push(temp)
				  console.log(temp)
				  console.log(data)
			  })
			}
			this.setState({graph_data:data})

		  })
	}
    render()
    {
        return(
            <div>
				 <Menu></Menu>
<div style={{ display: 'flex',maxWidth: 900 }}>
  <Chart
    width={1400}
    height={1300}
    chartType="ColumnChart"
    loader={<div>Loading Chart</div>}
    data={this.state.graph_data}
    options={{
      title: 'Sales of This Month',
      chartArea: { maxWidth: '75%',maHeight:'75%',backgroundColor:"black" },
      hAxis: {
        title: 'Date',
      },
      vAxis: {
        title: 'Sale',
      },
    }}
    legendToggle
  />

</div></div>)}
}
export default Graph;
