import React,{Component} from 'react'
import { MDBDataTableV5 } from 'mdbreact';
import Menu from './menu.component'
import axios from 'axios';

class Customer extends Component
{
constructor(props)
{
    super(props);
    this.state={
        datatable:{
            columns:[
                {
                    label: 'FirstName',
                    field: 'fname',
                    width: 150,
                  },
                  {
                    label: 'LastName',
                    field: 'lname',
                    width: 270,
                  },
                  {
                    label: 'Mobile',
                    field: 'mobile',
                    width: 200,
                  },
                  {
                    label: 'Date',
                    field: 'date',
                    sort: 'disabled',
                    width: 150,
                  }
            ],
            rows: [
              {
                fname:"dilip",
                lname:"joshi",
                mobile:"8975427620"
              }
            ],
        }
    }

    }

    update_rows=(response)=>{
      // console.log(response)
      // var arr=this.state.datatable.rows
      // response.forEach(element => {
      //   arr.push(element)
      // });        
      // console.log(arr);
      this.setState({
        datatable:{columns:[...this.state.datatable.columns],rows:[...this.state.datatable.rows,response]}
    })
    console.log(this.state)
  } 
    get_next=(response)=>{

      console.log(response)
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }

        axios.get(response,{headers:headers})
        .then(result=>{
          console.log(result.data.data.results)
          this.update_rows(result.data.data.results);
          if(result.data.data.links.next != null)
          {
            this.get_next(result.data.data.links.next)
          }
        })

    }

    componentDidMount=()=>{
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }
      axios.get('http://127.0.0.1:8000/customers/',{
        headers: headers
      })
      .then(response =>{
        this.update_rows(response.data.data.results);
        // this.get_next(response.data.data.links.next)
      })

    }

render()
{



    return(
      <div>
           <Menu/>
        <div style={{backgroundColor:"black",fontSize:"15px",color:"green"}}>
        <MDBDataTableV5 className="table-responsive" style={{backgroundColor:"black",fontSize:"20px",color:"white"}} hover entriesOptions={[10, 20, 25]} entries={10} pagesAmount={4} data={this.state.datatable} searchTop searchBottom={false} />
        </div>
      </div>

    )

}
}
export default Customer;
