import React,{Component} from 'react'
import { MDBDataTableV5 } from 'mdbreact';
import Menu from './menu.component'
import axios from 'axios';

class Customer extends Component
{
constructor(props)
{
    super(props);
    this.state={
        datatable:{
            columns:[
                {
                    label: 'FirstName',
                    field: 'fname',
                    width: 150,
                  },
                  {
                    label: 'LastName',
                    field: 'lname',
                    width: 270,
                  },
                  {
                    label: 'Mobile',
                    field: 'mobile',
                    width: 200,
                  },
                  {
                    label: 'Date',
                    field: 'date',
                    sort: 'disabled',
                    width: 150,
                  }
            ],
            rows: [
              {
                fname:"dilip",
                lname:"joshi",
                mobile:"8975427620"
              }
            ],
        }
    }

    }

    update_rows=(response)=>{
      this.setState(prevState => ({
        datatable: {                  
            ...prevState.datatable,   
            rows: response  
        }
    }))
  } 
    get_next=(response)=>{
      console.log(response)
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }

      while(response.next!=null)
      {
        axios.get(response.next,{headers:headers})
        .then(response=>{
          this.update_rows(response);
          this.response=response;
        })
      }
    }

    componentDidMount=()=>{
      const headers = {
        'Authorization': 'Token '+sessionStorage.getItem('token')
      }
      console.log(sessionStorage.getItem('token'))
      console.log(headers)
      axios.get('http://127.0.0.1:8000/customers/',{
        headers: headers
      })
      .then(response =>{
        this.update_rows(response.data.data.results);
        this.get_next(response)
      })

    }

render()
{



    return(
      <div>
           <Menu/>
        <div style={{backgroundColor:"black",fontSize:"15px",color:"green"}}>
        <MDBDataTableV5 className="table-responsive" style={{backgroundColor:"black",fontSize:"20px",color:"white"}} hover entriesOptions={[10, 20, 25]} entries={10} pagesAmount={4} data={this.state.datatable} searchTop searchBottom={false} />
        </div>
      </div>

    )

}
}
export default Customer;
